INSERT INTO public.traditions (id,title,short_description,long_description,created_at,updated_at) VALUES ('c4f0fda5-f0a7-532f-9a5f-a5e52bad00b6', 'Solemnity of Mary, Mother of God', 'A celebration of Mary''s motherhood of Jesus.', 'Observed on January 1st, this [Holy Day of Obligation](https://www.xavier.edu) honors the Blessed Virgin Mary as the ''Theotokos'' (God-bearer).', DEFAULT, DEFAULT), ('4a425ee8-157c-52d3-9221-c30e5835edd7', 'Epiphany of the Lord', 'Commemorates the revelation of Christ to the world.', 'Celebrates the [manifestation of the divine nature of Jesus](https://library.framingham.edu) to the Magi, signifying that the savior has come for all nations.', DEFAULT, DEFAULT), ('72e9e7e3-831f-51c8-88c4-0e784a800390', 'Feast of the Transfiguration', 'Commemorates Jesus'' radiant appearance on Mount Tabor.', 'Recalls the event where [Jesus’ physical appearance became brilliant](https://laverne.edu) as He spoke with Moses and Elijah, revealing His divine glory.', DEFAULT, DEFAULT), ('eca7e37f-f6a4-57b2-b924-68be1162f538', 'Feast of the Assumption', 'Celebrates Mary being taken body and soul into heaven.', 'A [Catholic solemnity](https://www.xavier.edu) on August 15th observing the Blessed Virgin Mary''s assumption into heavenly glory.', DEFAULT, DEFAULT), ('6a42c4dc-3cb8-5301-8998-3fffc5d14479', 'The Exaltation of the Holy Cross', 'Also known as Holy Rood Day, honoring the Cross of Christ.', 'Commemorates the finding and [annual elevation of the cross](https://buildfaith.org) used in the crucifixion of Jesus Christ.', DEFAULT, DEFAULT), ('9cee602c-5d32-5cab-a1fb-96ad9e1e0f3c', 'Reformation Day', 'Commemorates the start of the Protestant Reformation.', 'Observed on October 31st, marking the anniversary of [Martin Luther nailing the 95 Theses](https://www.faithward.org) to the church door in Wittenberg.', DEFAULT, DEFAULT), ('e1ceedfa-14a0-5620-97f6-07bde2e6f569', 'All Saints'' Day', 'Honoring all saints, known and unknown.', 'A day to honor all those who have achieved [lives of holiness](https://libguides.asu.edu) or were martyred for their faith. Also referred to as Solemnity of All Saints.', DEFAULT, DEFAULT), ('67c187dc-29cc-512c-8bb4-b6edc63706eb', 'Feast of the Immaculate Conception', 'Celebrates Mary being conceived without original sin.', 'A [Holy Day of Obligation](https://www.bbcatholic.org.au) on December 8th, celebrating the belief that Mary was preserved from original sin from her conception.', DEFAULT, DEFAULT);
