INSERT INTO public.traditions (id,title,short_description,long_description,created_at,updated_at) VALUES ('101fd85f-fa79-5e95-981d-efe3bc8f6d9d', 'Palm Sunday', 'The commemoration of Jesus'' triumphal entry into Jerusalem.', 'The [beginning of Holy Week](https://libguides.asu.edu), recalled with the blessing of palm branches to signify the crowds who welcomed Jesus.', DEFAULT, DEFAULT), ('fb6fc016-ef39-58ea-90a9-b5fad9f9cf4d', 'Easter', 'The celebration of the Passion, Death, and Resurrection of Jesus.', 'The [holiest period of the Church year](https://seaportshrine.org), including the Easter Vigil—the first liturgical celebration of the Resurrection—and Easter Sunday.', DEFAULT, DEFAULT), ('3ae7ff55-5de1-5ff2-8f41-914081a8dfcb', 'Feast of the Ascension', 'Celebrates Jesus Christ ascending into heaven.', 'Occurring 40 days after the Resurrection, this feast marks the moment [Jesus ascended bodily into heaven](https://www.christianity.com) in the presence of His apostles.', DEFAULT, DEFAULT), ('f4dc5001-f20d-5d07-aaf6-e4129e82aaf3', 'Pentecost', 'The descent of the Holy Spirit upon the Apostles.', 'Celebrated 50 days after Easter, it commemorates the [Holy Spirit coming to the disciples](https://libguides.asu.edu) as ''tongues of fire,'' marking the birth of the Church.', DEFAULT, DEFAULT), ('53a841fd-e6d8-5d78-87b1-17011b04f2f0', 'Christmas', 'The Nativity of Jesus Christ and the beginning of Christmastide.', 'A major solemnity celebrating the [birth of Jesus Christ](https://www.vaticannews.va) in Bethlehem. It marks the end of Advent and the start of the Christmas season.', DEFAULT, DEFAULT), ('9785d534-ad3a-525c-b5a0-d6ec32bbd360', 'Feast of Corpus Christi', 'The Solemnity of the Most Holy Body and Blood of Christ.', 'A feast honoring the real presence of the [body and blood of Jesus](https://github.com) in the Eucharist, traditionally celebrated after Trinity Sunday.', DEFAULT, DEFAULT), ('9743669d-fd60-5978-9772-81d833b6ee1a', 'First Sunday of Advent', 'The beginning of the liturgical year and a season of preparation.', 'Marks the start of the [Advent season](https://www.usccb.org worship/liturgical-year/advent), a time of expectant waiting and preparation for both the celebration of the Nativity of Christ at Christmas and the return of Christ at the Second Coming.', DEFAULT, DEFAULT), ('216cd372-c28d-5a51-b651-2d62b812e9ba', 'Solemnity of Christ the King', 'Honors Jesus Christ as Lord of all creation.', 'The final Sunday of the [liturgical year](https://www.facebook.com), emphasizing Christ''s sovereignty over the universe.', DEFAULT, DEFAULT), ('d4144788-8f08-5da3-b779-8401ccd69e5a', 'Totensonntag', 'Sunday of the Dead; a German Protestant day of remembrance.', 'The last Sunday before Advent in German Lutheran traditions, dedicated to [remembering those who have died](https://www.christianity.com) in the past year.', DEFAULT, DEFAULT);
